mapper.select:
SELECT SUM(credit_amount) AS total_credit_amount, SUM(used_credit_amount) AS total_used_credit_amount, SUM(remaining_credit_amount) AS total_remaining_credit_amount FROM rz_credit_management
and management_id like concat('%', #{managementId}, '%')
and scr_uuid like concat('%', #{scrUuid}, '%') and creditor like concat('%', #{creditor}, '%') and financial_institution = #{financialInstitution}
and credit_type = #{creditType} and credit_detail like concat('%', #{creditDetail}, '%')
and credit_amount like concat('%', #{creditAmount}, '%') and used_credit_amount like concat('%', #{usedCreditAmount}, '%') and remaining_credit_amount like concat('%', #{remainingCreditAmount}, '%')
and start_date between #{params.beginStartDate} and #{params.endStartDate}
and deadline between #{params.beginDeadline} and #{params.endDeadline}
and credit_cycle like concat('%', #{creditCycle}, '%')
and credit_state = #{creditState} and comment = #{comment} and uuid = #{uuid}

mapper.delete:
delete from rz_scr where scr_uuid in
(select scr_uuid from rz_credit_management where id in

    #{id}

);
delete from rz_credit_management where id in

    #{id}
