select.where:
1=1 and huankuanjine != 0
and manager_id = #{managerId}
and borrowing_unit = #{borrowingUnit}
and financial_institution =
    #{financialInstitution}

and qishu = #{qishu}

    and riqi between #{params.beginRiqi} and #{params.endRiqi}

and huankuanjine = #{huankuanjine}
and changhuanben = #{changhuanben}
and zhifulixi = #{zhifulixi}
and benjinshengyu = #{benjinshengyu}
and lilv = #{lilv}
and comment like concat('%', #{comment}, '%')

    and create_time between #{params.beginCreateTime} and #{params.endCreateTime}

and create_by = #{createBy}

    and update_time between #{params.beginUpdateTime} and #{params.endUpdateTime}

and update_by = #{updateBy}

mapper.update:
update rzhkjh_mingxi
manager_id = #{managerId}, borrowing_unit = #{borrowingUnit}, financial_institution = #{financialInstitution}, daikuanyongtu = #{daikuanyongtu}, qishu = #{qishu}, riqi = #{riqi}, huankuanjine = #{huankuanjine}, changhuanben = #{changhuanben}, zhifulixi = #{zhifulixi}, shouxufei = #{shouxufei}, benjinshengyu = #{benjinshengyu}, lilv = #{lilv}, comment = #{comment}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy},
where id = #{id}

mapper.select:
SELECT DATE_FORMAT(riqi, '%Y-%m') AS month, SUM(IFNULL(CAST(zhifulixi AS DECIMAL), 0)) AS total_interest, SUM(IFNULL(CAST(changhuanben AS DECIMAL), 0)) AS total_principal FROM rzhkjh_mingxi WHERE 1=1
AND DATE_FORMAT(riqi, '%Y-%m') >= #{param1}
AND DATE_FORMAT(riqi, '%Y-%m') <= #{param2}
GROUP BY DATE_FORMAT(riqi, '%Y-%m') order by riqi
